// Read context values
const isRunning = flow.get('isRunning') === true;
const startTime = flow.get('startTime'); // Expect an ISO string or timestamp

if (isRunning && startTime) {
    // Parse startTime into a Date object
    const start = new Date(startTime);
    if (isNaN(start.getTime())) {
        // Handle invalid startTime
        node.status({ fill: 'red', shape: 'ring', text: 'Invalid start time' });
        return null;
    }

    // Calculate elapsed time
    const now = new Date();
    const elapsedMs = now.getTime() - start.getTime();

    // Convert elapsed time to hours, minutes, and seconds
    const totalSeconds = Math.floor(elapsedMs / 1000);
    const hours = Math.floor(totalSeconds / 3600);
    const minutes = Math.floor((totalSeconds % 3600) / 60);
    const seconds = totalSeconds % 60;

    // Format as HH:MM:SS
    const formattedTime = [
        hours.toString().padStart(2, '0'),
        minutes.toString().padStart(2, '0'),
        seconds.toString().padStart(2, '0'),
    ].join(':');

    // Update payload and node status
    msg.payload = formattedTime;
    node.status({ fill: 'green', shape: 'dot', text: formattedTime });
    return msg;
} else if (!isRunning) {
    // Timer is stopped
    node.status({ fill: 'red', shape: 'ring', text: 'Timer Stopped' });
    return null;
} else {
    // Handle edge case: startTime is missing
    node.status({ fill: 'red', shape: 'ring', text: 'Missing start time' });
    return null;
}

let previousValue = flow.get('previousValue') || 0;

if (msg.payload === 1 && previousValue !== 1) {
    // First 1 received; send it
    flow.set('previousValue', 1);
    return msg; // Forward the message
} else if (msg.payload === 0 && previousValue !== 0) {
    // First 0 received; send it
    flow.set('previousValue', 0);
    return msg; // Forward the message
} else {
    // Repeated 1s or 0s, do nothing
    return null;
}

// Helper function for delay
function delay(ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
}

// Main async function
(async () => {
    if (msg.payload === true) {
        // Set a flag to indicate the backup test is running
        global.set('backupTestRunning', true);
        node.warn('Backup test started');

        try {
            // Step 1: Trigger start alarm
            global.set('alarm_status', 1);
            node.warn('Start alarm triggered');
            await delay(2000); // 2 seconds delay
            if (!global.get('backupTestRunning')) throw new Error('Backup test stopped');
            global.set('alarm_status', 0);
            node.warn('Start alarm stopped');

            // Step 2: Turn off mains input
            global.set('cmd_mains_input', 0);
            node.warn('Turning off mains input...');

            // Step 3: Wait for mains input to be confirmed as off
            while (global.get('sense_mains_input') !== 0) {
                if (!global.get('backupTestRunning')) throw new Error('Backup test stopped');
                node.warn('Waiting for sense_mains_input to confirm 0...');
                await delay(100); // Check every 100ms
            }
            node.warn('Mains input confirmed off');

            // Step 4: Wait for UPS output to be detected as 1
            while (global.get('sense_ups_output') !== 1) {
                if (!global.get('backupTestRunning')) throw new Error('Backup test stopped');
                node.warn('Waiting for sense_ups_output to confirm 1...');
                await delay(100); // Check every 100ms
            }
            node.warn('UPS output detected as 1');

            // Step 5: Start the backup timer
            let backupTime = 0;
            const interval = 1000; // 1-second interval

            node.warn('Measuring backup time...');
            while (global.get('sense_ups_output') === 1) {
                if (!global.get('backupTestRunning')) throw new Error('Backup test stopped');
                await delay(interval); // Wait 1 second
                backupTime++; // Increment backup timer
                global.set('BackupTime', backupTime);
                node.warn(`Backup time: ${backupTime} seconds`);
            }

            // Step 6: Stop backup test and log results
            node.warn('UPS output dropped to 0. Backup test stopped');
            global.set('BackupTime', backupTime);
            node.warn(`Total backup time: ${backupTime} seconds`);

        } catch (error) {
            // Handle process stop
            node.warn(error.message);
        } finally {
            // Reset backupTestRunning flag
            global.set('backupTestRunning', false);
            node.warn('Backup test process ended');
        }

    } else if (msg.payload === false) {
        // Stop the backup test
        node.warn('Stopping backup test...');
        global.set('backupTestRunning', false);
        global.set('alarm_status', 0);
        global.set('cmd_mains_input', 1); // Assuming 1 is the default or safe state
        node.warn('Backup test stopped and reset');
    }

    return null;
})();

