function processReportData(msg) {
    const payload = msg.payload;

    // Validate payload
    if (!Array.isArray(payload) || payload.length === 0) {
        console.error("Invalid or empty payload received.");
        return null;
    }

    // Extract common report details from the first entry
    const firstEntry = payload[0];
    const report = {
        test_report_id:firstEntry.test_report_id,
        settings: {
            client_name: firstEntry.client_name,
            standard: firstEntry.standard,
            ups_model: firstEntry.ups_model,
        },
        spec:{
           phase: firstEntry.phase,
            rating_va: firstEntry.rating_va,
             rated_voltage: firstEntry. rated_voltage,
              rated_current: firstEntry.rated_current,
               min_input_voltage: firstEntry.min_input_voltage,
                max_input_voltage: firstEntry.max_input_voltage,
                 pf_rated_current: firstEntry.pf_rated_current,
                  max_continous_amp: firstEntry.max_continous_amp,
                   overload_amp: firstEntry.overload_amp,

        },
        subreport_id: firstEntry.sub_report_id,
        test_name: firstEntry.test_name,
        test_description: firstEntry.test_description,
        test_result: firstEntry.test_result,
        measurements: [],
    };

    // Group data by measurement_unique_id
    const measurementMap = new Map();

    payload.forEach(entry => {
        const measurementId = entry.measurement_unique_id;

        // Check if measurement already exists
        if (!measurementMap.has(measurementId)) {
            measurementMap.set(measurementId, {
                m_unique_id: measurementId,
                time_stamp: new Date(entry.measurement_timestamp).getTime(),
                name: entry.measurement_name,
                mode: entry.mode,
                phase_name: entry.phase_name,
                load_type: entry.load_type,
                step_id: entry.step_id,
                load_percentage: entry.load_percentage,
                power_measures: [],
                steady_state_voltage_tol: null, // Placeholder
                voltage_dc_component: null, // Placeholder
                load_pf_deviation: null, // Placeholder
                switch_time_ms: null, // Placeholder
                run_interval_sec: entry.run_interval_sec,
                backup_time_sec: entry.backup_time_sec,
                overload_time_sec: null, // Placeholder
                temperature_1: null, // Placeholder
                temperature_2: null, // Placeholder
            });
        }

        // Add PowerMeasure to the appropriate measurement
        const powerMeasure = {
            type: entry.power_measure_type,
            voltage: entry.power_measure_voltage,
            current: entry.power_measure_current,
            power: entry.power_measure_power,
            energy: entry.power_measure_energy,
            pf: entry.power_measure_pf,
            frequency: entry.power_measure_frequency,
        };

        measurementMap.get(measurementId).power_measures.push(powerMeasure);
    });

    // Convert measurementMap to an array and assign to report
    report.measurements = Array.from(measurementMap.values());

    return report;
}



